import { useState } from "react";

function App() {
  const [todo, setTodo] = useState("");
  const todo_onChange = (e) => {
    setTodo(e.target.value);
  };
  const add_onClick = (e) => {
    e.preventDefault();

    if (!todo) {
      return;
    }
    setTodo("");
  };

  return (
    <form>
      <input
        type="text"
        value={todo}
        onChange={todo_onChange}
        placeholder="enter your to do"
      />
      <button onClick={add_onClick}>Add</button>
    </form>
  );
}

export default App;
