import { useEffect, useState } from "react";

function App() {
  const [isLoad, setIsLoad] = useState(false);
  const [coins, setCoins] = useState([]);
  const [selCoinIdx, setSelCoinIdx] = useState();
  const [input, setInput] = useState(0);

  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
      .then((r) => r.json())
      .then((json) => {
        console.log(json);
        if (json) {
          setCoins(json);
          setSelCoinIdx(0);
          setIsLoad(true);
        }
      });
  }, []);
  const select_onChange = (e) => {
    console.log("select_onChange", e.target.value);
    setSelCoinIdx(e.target.value);
  };
  return (
    <div>
      <h1>The Coins!{isLoad ? <span>({coins.length})</span> : null}</h1>
      <input type="number" value={input} />
      {!isLoad ? (
        <strong>Loading...</strong>
      ) : (
        <select onChange={select_onChange}>
          {coins.map((i, idx) => (
            <option key={i.id} value={idx}>
              {i.name} ({i.symbol}) : ${i.quotes.USD.price} USD
            </option>
          ))}
        </select>
      )}
      <br />
      {selCoinIdx}
      you can buy
    </div>
  );
}
export default App;
