import { useEffect, useState } from "react";

function App() {
  const [coins, setCoins] = useState([]);

  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
      .then((r) => r.json())
      .then((json) => {
        console.log(json);
        setCoins(json);
      });
  }, []);

  return (
    <div>
      <h1>
        The Coins!<span>({coins.length})</span>
      </h1>
      <select>
        {coins.map((i) => (
          <option>
            {i.name +
              " (" +
              i.symbol +
              ") : $" +
              i.quotes.USD.ath_price +
              " USD"}
          </option>
        ))}
      </select>
    </div>
  );
}
export default App;
